class CredentialsScreen extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      creds: []
    }

    console.log('THis is credentials screen')

    fetch('http://127.0.0.1:5000/get_creds/did:cred:5a13a432-bf10-11ea-aa79-f4d10855b531')
    .then(resp => resp.json())
    .then((json) => {
      console.log(json)
      this.setState({creds: json})
      console.log(this.state)
    })
    .catch((error) => {
      console.error(error);
    });
  }

  render() {
  return (
    <ScrollView
      style={styles.container}
      contentContainerStyle={styles.contentContainer}
    >
      <View style={styles.container}>
        <Text
          style={{
            color: "black",
            fontSize: 28,
            textAlign: "center",
            marginTop: 35,
          }}
        >
          My Credentials <Ionicons name="md-book" size={32} color="blue" />
        </Text>
      </View>
      <View style={styles.container}>
        <Text
          style={{
            color: "black",
            fontSize: 18,
            marginTop: 35,
          }}
          onPress={() => navigation.navigate("Cred")}
        >
          Credentials 1<Ionicons name="md-book" size={32} color="blue" />
        </Text>
        <Image
          source={{ uri: "https://i.imgur.com/TkIrScD.png" }}
          style={{
            width: 305,
            height: 159,
            marginTop: 40,
            marginLeft: 30,
          }}
        />

        <Text
          style={{
            color: "#888",
            fontSize: 18,
            marginTop: 20,
            textAlign: "center",
          }}
        >
          To share a photo from your phone with a friend, just press the button
          below!
        </Text>
      </View>
      <View style={styles.container}>
        <Text
          style={{
            color: "black",
            fontSize: 18,
            marginTop: 35,
          }}
          onPress={() => navigation.navigate("Cred")}
        >
          Credentials 2<Ionicons name="md-book" size={32} color="blue" />
        </Text>
        <Image
          source={{ uri: "https://i.imgur.com/TkIrScD.png" }}
          style={{
            width: 305,
            height: 159,
            marginTop: 40,
            marginLeft: 30,
          }}
        />

        <Text
          style={{
            color: "#888",
            fontSize: 18,
            marginTop: 20,
            textAlign: "center",
          }}
        >
          To share a photo from your phone with a friend, just press the button
          below!
        </Text>
      </View>

      <View>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate("Issue")}
        >
          <Text style={styles.buttonText}>
            New Issue?{" "}
            <Ionicons name="md-help" size={20} marginLeft={25} color="#fff" />
          </Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}
}
